namespace Fluidic;

public sealed partial class StringTemplateSourceGenerator
{
    private const string StringTemplateAttributeSourceCode = """
        #if !FLUIDIC_EXCLUDE_ATTRIBUTES

        // <auto-generated/>
        #nullable enable

        using System;

        namespace Fluidic;

        /// <summary>
        /// Marks a method a string template generator, this method will be created to generate
        /// into a string builder like object, based on a provided constant liquid template.
        /// </summary>
        [AttributeUsage(AttributeTargets.Method)]
        internal sealed class StringTemplateAttribute : Attribute
        {
            /// <summary>
            /// The liquid template to use for generating the string.
            /// </summary>
            public string? Template { get; set; }
            
            /// <summary>
            /// Initializes a new instance of the <see cref="StringTemplateAttribute"/> class
            /// </summary>
            public StringTemplateAttribute(string template)
            {
                Template = template;
            }
            
            /// <summary>
            /// Initializes a new instance of the <see cref="StringTemplateAttribute"/> class
            /// </summary>
            public StringTemplateAttribute()
            {
            }
        }
        #endif
        """;
}
